import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom'; // To redirect to the login page
import './css/UserPage.css';

const axiosInstance = axios.create({
  baseURL: 'http://localhost:5001', // API base URL
  withCredentials: true, // Enable sending cookies with requests
});

const UserPage = () => {
  const [user, setUser] = useState(null);
  const [error, setError] = useState('');
  const history = useHistory(); // To handle redirects

  useEffect(() => {
    const fetchUserDetails = async () => {
      try {
        // Fetch user details from the backend
        const response = await axiosInstance.get('/auth/user');

        if (response.status === 200) {
          setUser(response.data); // Set user details
        } else if (response.status === 401) {
          setError('You are not logged in. Redirecting to login...');
          setTimeout(() => {
            history.push('/login'); // Redirect to login after 2 seconds
          }, 2000);
        } else {
          setError('Could not fetch user details. Please try again later.');
        }
      } catch (err) {
        console.error('Error fetching user details:', err);
        setError('Could not fetch user details. Please try again.');
      }
    };

    fetchUserDetails();
  }, [history]);

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  if (!user) {
    return <div className="loading">Loading user details...</div>; // Show loading while fetching data
  }

  return (
    <div className="user-page">
      <h1>Welcome, {user.name}!</h1>
      {user.profilePicture ? (
        <img
          src={user.profilePicture}
          alt="Profile"
          className="profile-picture"
        />
      ) : (
        <img
          src="/default-profile.png" // Fallback to default if no profile picture
          alt="Profile"
          className="profile-picture"
        />
      )}
      <p>Email: {user.email}</p>
    </div>
  );
};

export default UserPage;

