import React, { useEffect, useState } from 'react';
import axios from 'axios';

const UserPage = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch the authenticated user details from the backend
    const fetchUser = async () => {
      try {
        const response = await axios.get('http://localhost:5001/auth/user', {
          withCredentials: true,
        });

        if (response.data) {
          setUser(response.data);
        } else {
          throw new Error('User data is empty');
        }
      } catch (err) {
        setError('Failed to fetch user details. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    fetchUser();
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;
  if (!user) return <p>No user data available. Please log in again.</p>;

  return (
    <div>
      <h1>Welcome, {user.name || 'Guest'}!</h1>
      <p><strong>Email:</strong> {user.email || 'N/A'}</p>
      <p><strong>Google ID:</strong> {user.googleId || 'N/A'}</p>
      {user.profilePicture ? (
        <img
          src={user.profilePicture}
          alt="Profile"
          style={{ borderRadius: '50%', width: '100px' }}
        />
      ) : (
        <p>No profile picture available.</p>
      )}
    </div>
  );
};

export default UserPage;

