import React from 'react';
import { useGoogleLogin } from '@react-oauth/google'; // Import the Google login hook

const MyCustomButton = ({ onClick, children }) => (
  <button onClick={onClick}>{children}</button>
);

const LoginPage = () => {
  const login = useGoogleLogin({
    onSuccess: async (tokenResponse) => {
      console.log('Login Success:', tokenResponse);

      // Fetch user info from Google using the access token
      try {
        const userInfoResponse = await fetch('https://www.googleapis.com/oauth2/v3/userinfo', {
          headers: {
            Authorization: `Bearer ${tokenResponse.access_token}`,
          },
        });

        if (!userInfoResponse.ok) {
          console.error(`Failed to fetch user info: ${userInfoResponse.statusText}`);
          return;
        }

        const userInfo = await userInfoResponse.json();
        console.log('Fetched User Info:', userInfo);

        // Now, redirect the user to the backend OAuth process
        window.location.href = 'http://localhost:5001/auth/google'; // Redirect to backend route to handle Google OAuth
      } catch (error) {
        console.error('Error during login or backend communication:', error);
      }
    },
    onError: (error) => {
      console.error('Login Failed:', error);
    },
  });

  return (
    <div>
      <h1>Login</h1>
      <MyCustomButton onClick={() => login()}>Sign in with Google</MyCustomButton>
    </div>
  );
};

export default LoginPage;
