import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './css/UserPage.css';

const UserPage = () => {
  const [user, setUser] = useState(null);
  const [error, setError] = useState('');
  const [events, setEvents] = useState([]);

  useEffect(() => {
    const fetchUserDetails = async () => {
      try {
        const response = await axios.get('http://localhost:5001/auth/user', {
          withCredentials: true, // Ensure cookies are sent with the request
        });

        setUser(response.data); // Set user details
      } catch (err) {
        console.error('Failed to fetch user details:', err);
        setError('Could not fetch user details. Please log in.');
      }
    };

    const fetchCalendarEvents = async () => {
      try {
        const response = await axios.get('http://localhost:5001/', {
          withCredentials: true, // Ensure cookies are sent with the request
        });

        setEvents(response.data); // Set events list
      } catch (err) {
        console.error('Failed to fetch calendar events:', err);
      }
    };

    fetchUserDetails();
    fetchCalendarEvents();
  }, []);

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  if (!user) {
    return <div>Loading...</div>;
  }

  return (
    <div className="user-page">
      <h1>Welcome, {user.name}!</h1>
      {user.profilePicture && (
        <img
          src={user.profilePicture}
          alt="Profile"
          className="profile-picture"
        />
      )}
      <p>Email: {user.email}</p>

      <div className="event-section">
        <h2>Upcoming Events</h2>
        {events.length === 0 ? (
          <p>No upcoming events</p>
        ) : (
          events.map((event) => (
            <div className="event-card" key={event.id}>
              <h3>{event.summary}</h3>
              <p>{event.description || 'No description available'}</p>
              <div className="event-time">
                <strong>Start:</strong> {new Date(event.start.dateTime).toLocaleString()}
                <br />
                <strong>End:</strong> {new Date(event.end.dateTime).toLocaleString()}
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default UserPage;

